  {name: "Geordi La Forge", rank: "Chief Engineer", race_id: 2, img_url: "http://vignette3.wikia.nocookie.net/startrek/images/5/5b/Geordi_La_Forge_2364.jpg/revision/latest?cb=20150223173136", bio: "Geordi La Forge was a male Human who was a noted Starfleet officer and engineer who served in the 24th century. He was most famous for serving as chief engineer aboard the USS Enterprise-D and USS Enterprise-E."},
  {name: "Worf", rank: "Commander", race_id: 1, img_url: "http://vignette3.wikia.nocookie.net/startrek/images/d/dc/Lt._Jr._Worf.jpg/revision/latest/scale-to-width-down/699?cb=20120423153015", bio: "Worf, son of Mogh, of the House of Martok, (2340- ), also known as Worf Rozhenko, was one of the single most influential people in Klingon and Federation politics of the late 24th century."},
  {name: "Goss", rank: "DaiMon",  race_id: 4, img_url: "http://vignette3.wikia.nocookie.net/memoryalpha/images/7/73/Goss.jpg/revision/latest/scale-to-width-down/700?cb=20130714192954&path-prefix=en", bio: "DaiMon Goss was a 24th century Ferengi and the commanding officer of a D'Kora-class marauder. In 2366, he demanded the right to negotiate on behalf of the Ferengi for rights to the Barzan wormhole. Though he had not been invited to the proceedings by the Barzans, Premier Bhavani agreed to allow their participation."},
  {name: "Hugh", rank: "Drone", race_id: 3, img_url: "http://vignette3.wikia.nocookie.net/memoryalpha/images/4/40/Hugh-Drone.jpg/revision/latest/scale-to-width-down/700?cb=20141207193522&path-prefix=en", bio: "Hugh, formerly Third of Five, was a former Borg drone who was rescued by the USS Enterprise-D in 2368."}
